import { css } from 'styled-components';

export default {
  container: css`
    background: white;
    border-top-right-radius: 10px;
    border-bottom-left-radius: 10px;
    cursor: pointer;
    position: relative;
    padding: 10px 10px;
    font-size: 18px;
    transition:all 1s;
    width: 40%;

    &:after, &:before {
      content: " ";
      width: 10px;
      height: 10px;
      position: absolute;
      border : 0px solid #fff;
      transition: all 1s;
    }
  `,
  image: css`
    transition: opacity .4s linear;
    width: 80%;
  `,
};
