import React from "react";
import PropTypes from 'prop-types';
import styled from 'styled-components';

import Styles from './characterFilm.style';

const Container = styled.div`${Styles.container}`;
const Figure = styled.figure`${Styles.figure}`;
const Image = styled.img`${Styles.image}`;
const Section = styled.section`${Styles.section}`;

const IMAGE_PATH = 'https://starwars-visualguide.com/assets/img/films/';
const IMAGE_EXT = '.jpg';

const characterFilm = ({
  id, title, description, director, producer, releaseDate
}) => {
  const imageUrl = `${IMAGE_PATH}${id}${IMAGE_EXT}`;

  return (
    <>
      <Container>
        <Figure>
          <Image src={imageUrl} />
        </Figure>
        <Section>
          <div class="title">{title}</div>
          <div>
            <span class="sub">Date Created</span>
            <div class="text">{releaseDate}</div>
          </div>
          <div>
            <span class="sub">Director</span>
            <div class="text">{director}</div>
          </div>
          <div>
            <span class="sub">Producer(s)</span>
            <div class="text">{producer}</div>
          </div>
          <div>
            <span>Opening Crawl</span>
            <div>{description}</div>
          </div>
        </Section>
      </Container>
    </>
  );
};

characterFilm.defaultProps = {

}

export default characterFilm;
