import { css } from 'styled-components';

export default {
  container: css`
    border-top-right-radius: 10px;
    border-bottom-left-radius: 10px;
    cursor: pointer;
    padding: 6px 6px;
    position: relative;
    transition: all .1s;
    width: 34%;

    &:after, &:before {
      border : 0px solid #fff;
      content: "";
      height: 10px;
      position: absolute;
      transition: all .5s;
      width: 10px;
    }

    &:after {
      border-left: 5px solid black;
      border-top: 5px solid black;
      left: 0;
      top: 0;
    }

    &:before {
      border-bottom: 5px solid black;
      border-right: 5px solid black;
      bottom: 0;
      right: 0;
    }

    &:hover {
      border-bottom-left-radius: 0px;
      border-top-right-radius: 0px;

      & figure {
        background: #1abc9c;

        & img {
          opacity: .4;
        }
      }

      & section {
        opacity: 1;
      }

      &:before, &:after {
        box-sizing: border-box;
        height: 100%;
        width: 100%;
      }
    }
  `,
  figure: css`
    display: flex;
    margin: 0;
    padding: 0;
    width: 100%;
  `,
  image: css`
    width: 100%;
  `,
  section: css`
    color: white;
    height: 100%;
    left: 0px;
    padding: 20px 20px;
    position: absolute;
    top: 0px;
    width: 100%;
    opacity: 0;

    & .title {
      font-size: 18px;
      font-weight: bold;
      text-align: center;
    }
  `
};
