import { css } from 'styled-components';

const IMAGE_PATH = 'https://starwars-visualguide.com/assets/img/films/';
const IMAGE_EXT = '.jpg';

export default {
  image: ({ imageId }) => css`
    background: url(${IMAGE_PATH}${imageId}${IMAGE_EXT}) cover;
    width: 400px;
    height: 540px;
  `,
};
