import { css } from 'styled-components';

export default {
  container: css`
    cursor: pointer;
    width: 40%;
    &:after, &:before {
      content: "";
      width: 10px;
      height: 10px;
      position: absolute;
      border : 0px solid #fff;
      transition: all 1s;
    }
  `,
  image: css`
    transition: opacity .4s linear;
    width: 80%;
  `,
};
