import { css } from 'styled-components';

export default {
  container: css`
    cursor: pointer;
    width: 40%;
    position:relative;
    padding:10px 20px;
    background:white;
    font-size:28px;
    border-top-right-radius:10px;
    border-bottom-left-radius:10px;
    transition:all 1s;
    &:after, &:before {
      content: "";
      width: 10px;
      height: 10px;
      position: absolute;
      border : 0px solid #fff;
      transition: all 1s;
    }
  `,
  image: css`
    transition: opacity .4s linear;
    width: 80%;
  `,
};
