import { css } from 'styled-components';

export default {
  container: css`
    background: white;
    border-top-right-radius: 10px;
    border-bottom-left-radius: 10px;
    cursor: pointer;
    font-size: 18px;
    padding: 6px 6px;
    position: relative;
    transition: all .1s;
    width: 30%;

    &:after, &:before {
      border : 0px solid #fff;
      content: "";
      height: 10px;
      position: absolute;
      transition: all .5s;
      width: 10px;
    }

    &:after {
      border-left: 5px solid black;
      border-top: 5px solid black;
      left: 0;
      top: 0;
    }

    &:before {
      border-bottom: 5px solid black;
      border-right: 5px solid black;
      bottom: 0;
      right: 0;
    }

    &:hover {
      border-bottom-left-radius: 0px;
      border-top-right-radius: 0px;

      &:before, &:after {
        box-sizing: border-box;
        height: 100%;
        width: 100%;
      }
    }
  `,
  image: css`
    transition: opacity .4s linear;
    width: 100%;
  `,
  legend: css`
    background: #1abc9c;
    left: 0px;
    opacity: .5;
    position: absolute;
    top: 0px;
    width: 100%;
    height: 100%;
  `
};
