import React from "react";
import PropTypes from 'prop-types';
import styled from 'styled-components';

import Styles from './characterFilm.style';

const Figure = styled.figure`${Styles.container}`;
const Image = styled.img`${Styles.image}`;
const Legend = styled.div`${Styles.legend}`;

const IMAGE_PATH = 'https://starwars-visualguide.com/assets/img/films/';
const IMAGE_EXT = '.jpg';

const characterFilm = ({
  id, title, description, producer, releaseDate
}) => {
  const imageUrl = `${IMAGE_PATH}${id}${IMAGE_EXT}`;

  return (
    <>
      <div>
        <Figure>
          <Image src={imageUrl} />
        </Figure>
        <Legend>
          <div>{title}</div>
          <div>Date Created: {releaseDate}</div>
          <div>Director: {producer}</div>
          <div>{description}</div>
        </Legend>
      </div>
    </>
  );
};

characterFilm.defaultProps = {

}

export default characterFilm;
